import React from "react";
import { connect } from "react-redux";
import { fetchAnnots } from "../actions";

class AnnotList extends React.Component {
  componentDidMount() {
    this.props.fetchAnnots();
  }

  renderList() {
    return this.props.annots.map(annot => {
      return (
        <div className="item" key={annot.id}>
          <div className="content">
            <div className="description">
              <h2>{annot.user}</h2>
              <p>{annot.text}</p>
              <h5>{annot.tags.join()}</h5>
            </div>
          </div>
        </div>
      );
    });
  }

  renderTag() {
    var a = [];

    const b = this.props.annots;
    for (var i = 0; i < b.length; i++) {
      a.push(b[i].tags);
    }
    console.log("aaa", a);
    var flat = [];

    for (var j = 0; j < a.length; j++) {
      console.log("hahhha", a[j]);
      flat = flat.concat(a[j]);
      console.log("bbb", flat);
    }
    console.log("bbb", flat);
    return (
      <div>
        <h3>{flat.join()}</h3>
      </div>
    );
  }

  render() {
    console.log(this.props.annots);
    return (
      <div>
        <div className="ui relaxed divided list">{this.renderList()}</div>
        <div>{this.renderTag()}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { annots: state.annots };
};

export default connect(
  mapStateToProps,
  { fetchAnnots }
)(AnnotList);
