import "../css/Sidebar.css";
import React from "react";
import {
  Button,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar
} from "semantic-ui-react";

class AnnotSidebar extends React.Component {
  //   renderFullAnnot() {
  //     console.log("full annot info:", this.props.fullAnnots.annotData);
  //     return (
  //       <div>
  //         <p>{this.props.fullAnnots.annotData.text}</p>
  //       </div>
  //     );
  //   }

  render() {
    const annotData = this.props.fullAnnots.annotData;
    if (annotData.user) {
      var user = annotData.user.substring(
        annotData.user.indexOf(":") + 1,
        annotData.user.indexOf("@")
      );
      var tags = annotData.tags.join(" ");
    }

    return (
      <div className="sidebar-content">
        <Menu.Item>
          <div className="ui">
            <div className="basic-info">
              <p className="user-info">
                <i className="user icon" />
                <span className="user-name">Annotator:</span> {user}
              </p>
              <p className="annot-info">
                <i className="linkify icon" />
                Visit annotation in context
              </p>
              <p className="annot-info">
                <i className="tag icon" />
                Tags
              </p>
              <p className="tag-info">{tags}</p>
            </div>
          </div>
        </Menu.Item>

        <Menu.Item>{annotData.text}</Menu.Item>
      </div>
    );
  }
}

export default AnnotSidebar;
