import "../css/AnnotList.css";
import React from "react";
import { connect } from "react-redux";
import { fetchAnnots } from "../actions";

class AnnotList extends React.Component {
  componentDidMount() {
    this.props.fetchAnnots();
    // const token = "6879-QLwKBhX26Ja-nDyHvT5HslkRkuU41tr3W7GscMy3yVY";

    // fetch("https://hypothes.is/api/profile", {
    //   method: "GET",
    //   headers: {
    //     Accept: "application/json",
    //     Authorization: "Bearer" + token
    //   }
    // })
    //   .then(res => {
    //     if (res.ok) {
    //       console.log("ok", res.json);
    //       console.log("okkkk", res);
    //       return res.json();
    //     } else {
    //       throw Error(res.statusText);
    //     }
    //   })
    //   .then(json => {
    //     console.log("json", json);
    //   })
    //   .catch(err => console.log("error", err));
  }

  renderList() {
    return this.props.annots.map(annot => {
      return (
        <div className="item" key={annot.id}>
          <div className="content">
            <div className="description">
              <h2>{annot.user}</h2>
              <p>{annot.text}</p>
              <h5>{annot.tags.join()}</h5>
            </div>
          </div>
        </div>
      );
    });
  }

  renderTag() {
    var a = [];
    const b = this.props.annots;
    for (var i = 0; i < b.length; i++) {
      a.push(b[i].tags);
    }
    var flat = [];
    for (var j = 0; j < a.length; j++) {
      flat = flat.concat(a[j]);
    }
    // console.log("bbb", flat);
    var uniqueTags = flat.filter((elem, index, self) => {
      return index === self.indexOf(elem);
    });
    // console.log("unique", uniqueTags);
    var tagCounts = [];
    for (var k = 0; k < flat.length; k++) {
      var tagElement = flat[k];
      tagCounts[tagElement] = tagCounts[tagElement]
        ? tagCounts[tagElement] + 1
        : 1;
    }
    // console.log("counts:", tagCounts);
    const tagArray = [];
    for (var n = 0; n < uniqueTags.length; n++) {
      tagArray.push({
        text: uniqueTags[n],
        count: tagCounts[uniqueTags[n]]
      });
    }
    // console.log("lalal", tagArray);
    return tagArray.map(tag => {
      return (
        <div className="ui horizontal bulleted list link" key={tag.text}>
          <div className="item">
            <p>
              {tag.text} <a>{tag.count}</a>
            </p>
          </div>
        </div>
      );
    });
  }

  render() {
    console.log(this.props.annots);
    return (
      <div className="dashboard">
        <div className="dashboard-container">
          <div className="dash-header">
            <p>Top Tags</p>
            <div>
              <div className="ui search">
                <div className="ui icon input">
                  <input
                    className="prompt"
                    type="text"
                    placeholder="Search Tags ..."
                  />
                  <i className="search icon" />
                </div>
              </div>
            </div>
          </div>
          <div className="tag-list">
            <div>{this.renderTag()}</div>
          </div>

          <div className="ui relaxed divided list">{this.renderList()}</div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { annots: state.annots };
};

export default connect(
  mapStateToProps,
  { fetchAnnots }
)(AnnotList);
