import React from "react";
import {
  Button,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar
} from "semantic-ui-react";

class AnnotSidebar extends React.Component {
  //   renderFullAnnot() {
  //     console.log("full annot info:", this.props.fullAnnots.annotData);
  //     return (
  //       <div>
  //         <p>{this.props.fullAnnots.annotData.text}</p>
  //       </div>
  //     );
  //   }

  render() {
    const annotData = this.props.fullAnnots.annotData;
    if (annotData) {
      var user = annotData.user.substring(
        annotData.user.indexOf(":") + 1,
        annotData.user.indexOf("@")
      );
    }

    return (
      <div>
        {/* <Menu.Item as="a">
          <Icon name="home" />
          Home
        </Menu.Item>
        <Menu.Item as="a">
          <Icon name="gamepad" />
          Games
        </Menu.Item>
        <Menu.Item as="a">
          <Icon name="camera" />
          Channels
        </Menu.Item> */}
        <Menu.Item>
          <div className="ui">
            <div className="content">
              <a className="header">
                <i className="user icon" />
                Annotator: {user}
              </a>
            </div>
          </div>
        </Menu.Item>

        <Menu.Item>{annotData.text}</Menu.Item>
      </div>
    );
  }
}

export default AnnotSidebar;
