import "../css/Login.css";
import React from "react";
import { Link } from "react-router-dom";

const Login = () => {
  this.state = {
    username: "",
    error: false
  };

  onInputChange = e => {
    console.log("username:", e.target.value);
    this.setState({ username: e.target.value });
  };

  onLoginSubmit = e => {
    e.preventDefault();
    console.log("usernameya:", this.state.username);
  };

  return (
    <div className="ui container login-container">
      <h4>Login With Your Hypothesis User Name</h4>
      <div className="login-text">
        <form className="ui form" onSubmit={this.onLoginSubmit}>
          <div className="field">
            <label>Hypothesis User Name</label>
            <input
              onChange={this.onInputChange}
              type="text"
              name="api-token"
              placeholder="User Name..."
            />
          </div>
          <div className="field">
            {/* <label className="api-label">
              You can generate a personal API token via Hypothesis Settings >
              Developer
            </label> */}
            <label className="api-label">
              After you logged in Hypothesis, you can find your user name in the
              top navigation bar.
            </label>
          </div>
          <button className="ui primary button" type="submit">
            Login Now
          </button>
        </form>

        {/* <Link to={{ pathname: "/dashboard", state: { foo: "bar" } }}>
          <button className="ui primary button">Set Token</button>
        </Link> */}
      </div>
      <div className="ui horizontal divider">Or</div>
      <Link to="">
        <button className="ui primary button">Create Hypothesis Account</button>
      </Link>
    </div>
  );
};

export default Login;
