import React from "react";
import { connect } from "react-redux";
import { fetchAnnots } from "../actions";

class AnnotList extends React.Component {
  componentDidMount() {
    this.props.fetchAnnots();
  }

  renderList() {
    return this.props.annots.map(annot => {
      return (
        <div className="item" key={annot.id}>
          <div className="content">
            <div className="description">
              <h2>{annot.user}</h2>
              <p>{annot.text}</p>
            </div>
          </div>
        </div>
      );
    });
  }

  renderTags() {
    return this.props.annots.map(annot => {
      annot.tags.join(",");
      return (
        <div key="annot.text">
          <h5>{annot.tags}</h5>
        </div>
      );
    });
  }

  render() {
    console.log(this.props.annots);
    return (
      <div>
        <div className="ui relaxed divided list">{this.renderList()}</div>
        <div>{this.renderTags()}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { annots: state.annots };
};

export default connect(
  mapStateToProps,
  { fetchAnnots }
)(AnnotList);
